import React, { useState } from "react";
import { getCollegesByRank } from "../api/colleges"; // Import the API function

const Home = () => {
    const [rank, setRank] = useState("");
    const [colleges, setColleges] = useState([]);
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(""); // Reset error state

        try {
            const fetchedColleges = await getCollegesByRank(rank); // Fetch colleges by rank
            setColleges(fetchedColleges); // Set colleges in state
        } catch (err) {
            console.error(err);
            setError("Unable to fetch colleges. Please try again later.");
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>Campus Navigator</h1>
            <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
                <label>
                    Enter Your Rank:
                    <input
                        type="number"
                        value={rank}
                        onChange={(e) => setRank(e.target.value)}
                        placeholder="Enter rank"
                        style={{ margin: "0 10px", padding: "5px" }}
                    />
                </label>
                <button type="submit" style={{ padding: "5px 10px" }}>
                    Find Colleges
                </button>
            </form>

            {error && <p style={{ color: "red" }}>{error}</p>}

            {colleges.length > 0 && (
                <table border="1" cellPadding="10" style={{ marginTop: "20px", width: "100%" }}>
                    <thead>
                        <tr>
                            <th>College Name</th>
                            <th>Rank</th>
                            <th>Location</th>
                            <th>Cutoff</th>
                            <th>Placements</th>
                        </tr>
                    </thead>
                    <tbody>
                        {colleges.map((college, index) => (
                            <tr key={index}>
                                <td>{college.name}</td>
                                <td>{college.nirfRank}</td>
                                <td>{college.place}</td>
                                <td>{college.cutoff}</td>
                                <td>{college.placements}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            {colleges.length === 0 && !error && (
                <p>No colleges found for the given rank. Try another rank.</p>
            )}
        </div>
    );
};

export default Home;
