import React, { useState } from "react";
import { getCollegesByRank } from "../api/colleges";

const Home = () => {
    const [rank, setRank] = useState(""); // To store the rank entered by the user
    const [colleges, setColleges] = useState([]); // To store the fetched colleges
    const [error, setError] = useState(""); // To handle errors

    const handleFetchColleges = async () => {
        try {
            setError(""); // Clear any previous errors
            console.log("Fetching colleges for rank:", rank); // Debug log
            const data = await getCollegesByRank(rank); // API call
            console.log("Fetched colleges:", data); // Debug log
            setColleges(data); // Update the state with fetched colleges
        } catch (err) {
            console.error("Error fetching colleges:", err);
            setError("Unable to fetch colleges. Please try again later.");
        }
    };

    return (
        <div>
            <h1>Campus Navigator</h1>
            <label>Enter Your Rank:</label>
            <input
                type="number"
                value={rank}
                onChange={(e) => setRank(e.target.value)} // Update rank state
            />
            <button onClick={handleFetchColleges}>Find Colleges</button>

            {error && <p style={{ color: "red" }}>{error}</p>} {/* Show error */}
            <ul>
                {colleges.map((college, index) => (
                    <li key={index}>
                        {college.name} - {college.place} (Cutoff: {college.cutoff})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Home;
