import React, { useState } from "react";
import { getCollegesByRank } from "../api/colleges";
import CollegeTable from "../components/CollegeTable";

const Home = () => {
    const [rank, setRank] = useState("");
    const [colleges, setColleges] = useState([]);
    const [error, setError] = useState("");

    const handleFindColleges = async () => {
        try {
            const data = await getCollegesByRank(rank);
            setColleges(data); // Ensure the response is saved correctly
            setError("");
        } catch (err) {
            setError("Unable to fetch colleges. Please try again later.");
        }
    };

    return (
        <div>
            <h1>Campus Navigator</h1>
            <input
                type="text"
                placeholder="Enter Your Rank"
                value={rank}
                onChange={(e) => setRank(e.target.value)}
            />
            <button onClick={handleFindColleges}>Find Colleges</button>
            {error && <p>{error}</p>}
            <CollegeTable colleges={colleges} />
        </div>
    );
};

export default Home;
